import BaseClass from '../base';
// TODO: Experiment

/**
 * Simple Router Experiment
 * @example
 // configuration
Router.config({ mode: 'history'});

// returning the user to the initial state
Router.navigate();

// adding routes
Router
.add(/about/, function() {
		console.log('about');
})
.add(/products\/(.*)\/edit\/(.*)/, function() {
		console.log('products', arguments);
})
