/*globals Request, Promise*/
'use strict';
import BaseClass from '../base';
//import fetch from 'fetch';

/**
 * HTTP class provides an abstraction layer for HTTP calls.
 * @example
 * var $http = new px.mobile.HTTP('http1', {
 *	baseUrl: window.location.origin
 * });
 */
export default class HTTP extends BaseClass {

	/**
	 * Create a new instance of a HTTP service.
	 * @constructor
	 * @param {String} name - The name of the service
	 * @param {Object} options - The default options to pass
	 * @return {HTTP} An instance of the HTTP class
	 */
	constructor(name = 'http', options = {
		baseUrl: '/default'
	}) {
		super(name, options);

		if (!options.baseUrl) {
			//throw new Error('HTTP: Must provide a baseUrl');
				this.log.logApi('[HTTP] - Using default baseUrl - /default');
		}

		return this;
	}

	/**
	 *	I handle checking the response status code of a HTTP request.
	 * @param {Response} response A Response object
	 * @return {Response} The original response
	 */
	checkStatus(response) {

		if (response.status >= 200 && response.status < 300) {
			return response;
		} else {
			var error = new Error(response.statusText);
			error.response = response;
			return response;
		}
	}

	/**
	 * I handle parsing the JSON of a response.
	 * @param {Response} response A Response object
	 * @return {Response} The original response with a data property that is the parsed JSON
	 */
	parseJSON(response) {
		if (!response) {
			throw new Error('Must pass a response object to parseJSON!');
		}
		return response.json().then(function(json) {
			response.data = json;
			return response;
		});
	}

	/**
	 *	I make a HTTP request for JSON.
	 * @example
	 * $http.getJSON('/default/_all_docs', {limit: 5}).then(function(resp){
	 *		//handle json
	 * }).catch(function(err){
	 *		//handle error
	 * });
	 * @param {String} url - The url
	 * @param {Object} options - Request options object
	 * @return {Promise} A promise that resolves a response
	 */
	getJSON(url = '', options = {}) {
		return fetch(url, options || {
			method: 'GET'
		})
			.then(this.checkStatus)
			.then(this.parseJSON);
	}

	/**
	 * The response object has these properties:
	 * @example
	 * $http.request('/default', {
	 *		method: 'POST',
	 *		data: data
	 * }).then(function(resp){
	 *		//handle response
	 * }).catch(function(err){
	 *		//handle error
	 * });
	 * @param {String} url - The url
	 * @param {Object} options - Options to pass
	 * @return {Promise} A promise that resolves a response
	 */
	request(url, options) {
		var _this = this;
		var config = this.utils.extend({
			data: null,
			params: null,
			body: null,
			method: 'GET',
			headers: {
				'Content-Type': 'application/json'
			}
		}, options);

		if (options.data) {
			config.body = JSON.stringify(options.data);
			delete config.data;
		}


		url = this.utils.resolveURL(this.baseUrl, url);
		//url = this.utils.resolveURL(url);

		if (options.params) {
			url = this.utils.resolveURL(url, options.params);
			delete options.params;
		}

		//this.log.logApi(`request => ${url}`, config);
		this.log.logHttp(config.method, url);

		let benchmark = this.log.logTime('request');
		return new Promise((resolve, reject) => {
			return fetch(url, config)
				.then((resp) => {
				this.log.logHttp(resp.status + ' ' + benchmark.end(), resp, true);
				//return this.parseJSON(resp).then(resolve, reject);
				resp.data = {};
				resolve(resp);
			}, reject);
		});
	}

	/**
	 * I make a HTTP GET request.
	 * @example
	 * $http.get('/default/_all_docs', {limit: 5}).then(function(resp){
	 *		//handle resp:Response
	 * }).catch(function(err){
	 *		//handle error
	 * });
	 * @param {String} url The url
	 * @param {Object} options Options to pass
	 * @return {Promise} A promise that resolves a response
	 */
	get(url, options = {}) {
		this.log.logApi('get', options);
		return this.request(url, options).then(this.parseJSON);
	}

	/**
	 * I make a HTTP PUT request.
	 * @example
	$http.put('/default/id', {name: 'value'}).then(function(resp){
		//handle resp:Response
	}).catch(function(err){
		//handle error
	});
	 * @param {String} url The url
	 * @param {Object} options Options to pass
	 * @return {Promise} A promise that resolves a response
	 */
	put(url, data = null, options = {}) {
		this.log.logApi('put', data);
		return this.request(url, this.utils.extend({
			method: 'PUT',
			data: data
		}, options));
	}

	/**
	 * I make a HTTP POST request.
	 * @example
 $http.post('/default', {name: 'value'}).then(function(resp){
	 //handle resp:Response
 }).catch(function(err){
	 //handle error
 });

	 * @param {String} url The url
	 * @param {Object} options Options to pass
	 * @return {Promise} A promise that resolves a response
	 */
	post(url, data = null, options = {}) {
		return this.request(url, this.utils.extend({
			method: 'POST',
			data: data
		}, options));
	}

	/**
	 * I make a HTTP DELETE request.
	 * @example
 $http.delete('/default/id').then(function(resp){
	 //handle resp:Response
 }).catch(function(err){
	 //handle error
 });

	 * @param {String} url The url
	 * @param {Object} options Options to pass
	 * @return {Promise} A promise that resolves a response
	 */
	delete(url = '', options = {}) {
		return this.request(url, this.utils.extend({
			method: 'DELETE'
		}, options));
	}

	/**
	 * I make a HTTP HEAD request.
	 * @example
	$http.head('/default/id').then(function(resp){
		//handle resp:Response
	}).catch(function(err){
		//handle error
	});

	 * @param {String} url The url
	 * @param {Object} options Options to pass
	 * @return {Promise} A promise that resolves a response
	 */
	head(url = '', options = {}) {
		return this.request(url, this.utils.extend({
			method: 'HEAD'
		}, options));
	}

}

'use strict';

import BaseClass from '../base';
import HTTP from './http';
import * as utils from '../utils/utils';
import Logger from '../utils/log';

/**
 * Model class provides event dispatching.
 * @example
 * var model = new Model();
 * @param {String} id - The id of the model.
 * @param {Object} options - Options for the model
 */
export default class Model extends BaseClass {

	constructor(id, options) {
		id = id || utils.uuid();
		options = options || {};

		super(id, options);

		this.uuid = utils.uuid();
		this.baseUrl = options.baseUrl || '/default';
		this.defaults = options.defaults || {};
		this.idField = options.idField || '_id';
		this.scope = options.data || {};
		this.scope[options.idField] = id;

		this.adapter = options.adapter || HTTP;
		this.adapter = new this.adapter(id, options);

		this.log = new Logger('Model:' + id, {
			colors: {
				debug: 'color:blue'
			}
		});
		this.log.logApi('constructor', options);
	}

	url() {
		var url = `/${encodeURIComponent( this.get('_id') )}`;

		if (this.get('_rev')) {
			url += `?rev=${this.get('_rev')}`;
		}
		this.log.logApi('url()', url);
		return url;
	}

	has(attribute) {
		//this.log.logApi('has', attribute);
		return this.scope.hasOwnProperty(attribute);
	}

	get(attribute) {
		//this.log.logApi('has', attribute);
		if (this.has(attribute)) {
			return this.scope[attribute];
		} else {
			return false;
		}
	}

	set(attributes, force = true) {
		for (var key in attributes) {
			if (force) {
				this.scope[key] = attributes[key];
			}
			if (this.has(key)) {
				this.scope[key] = attributes[key];
			}
		}
		return this;
	}

	toJSON() {
		this.log.logApi('toJSON', this.scope);
		return JSON.stringify(this.scope);
	}

	clone() {
		return new this.constructor(this.options);
	}

	parse(resp) {
		this.log.logApi('parse', resp);
		if (resp.data) {
			this.scope = resp.data;
		}
		return resp;
	}

	fetch(options) {
		var self = this;
		this.log.logApi('fetch', options);
		return this.adapter.get(this.url(), options || {}).then(function(resp) {
			return self.parse(resp);
		});
	}

	save(options) {
		this.log.logApi('save', options);
		return this.adapter.put(`${this.url()}`, this.scope);
	}

	destroy(rev) {
		this.log.logApi('destroy', rev);
		return this.adapter.delete(`${this.url()}`);
	}

	sync(options) {
		this.log.logApi('sync', options);
	}

	static extend(obj) {
		return super.extend(this, obj);
	}
}

import Logger from '../utils/log';
import * as utils from '../utils/utils';


export default class pmPage {
  createdCallback() {
    const logger = new Logger(this.localName, {
      colors: {
        debug: 'color:orange'
      }
    });
    utils.addMixin(logger, this);

    this.properties = {
      //Title of the page
      title: {
        type: String,
        value: 'Page Title'
      },
      //Text for the back button
      backText: {
        type: String,
        value: null
      },
      //Enable back button
      backLink: {
        type: String,
        value: null
      },
      //Path to remote view that will be loaded
      href: {
        type: String,
        notify: true,
        value: null,
        observer: '_tmplChanged'
      },
      //If the page is the current page.
      active: {
        type: Boolean,
        value: false
      },
      //If the page is a dialog
      modal: {
        type: Boolean,
        value: false
      },
      //with URL query parameters. If your page URL is "about.html?id=10&count=20&color=blue"
      query: {
        type: Object
      },
      //The context that was passed for this page when using pxm-pages
      context: {
        type: Object
      },
      //View instance that contains this page (if this View was initialized)
      view: {
        type: Object
      },
      //Page Data object of the previously active page
      fromPage: {
        type: Object
      },
      route: {
        type: String
      },
      //Contains string URL of just loaded page
      url: {
        type: String
      },
      //Link to Page HTMLElement
      container: {
        type: String
      }
    };

    this.createShadowRoot().innerHTML =
      `
					<style>
						:host {
					    display: block;
					    box-sizing: border-box;
					    -webkit-transition: all 0.4s ease-out;
					    transition: all 0.4s ease-out;
					  }
						:host header{
						  padding: 10px;
						}
					  :host .close-btn{
					    cursor: pointer;
					    position: absolute;
					    right: .1em;
					    top: .2em;
					    width: 44px;
					    height: 44px;
					    display: block;
					  }
				    :host .hamburger {
				      width: 20px;
				      height: 2px;
				      background: #000;
				      display: block;
				      position: absolute;
				      top: 50%;
				      left: 15%;
				      transition: transform 300ms;
				    }
					  :host .hamburger-1 {
					    transform: translate3d(0, 0, 0) rotate(45deg);
					  }
					  :host .hamburger-2 {
					    transform: translate3d(0, 0, 0) rotate(-45deg);
					  }
					</style>
					<div class="page-content overthrow">
						<content></content>
					</div>
					`;

    this.logApi('created', this.id);

  }
  ready() {
    this.logApi('ready', this.id);
    if (this.modal) {
      this.toggleClass('modal');
    }
    //return this.emit('init', this);
  }
  attachedCallback() {
    //  return this.emit('after:init', this);
  }
  detachedCallback() {

  }
  attributeChangedCallback(prop, oldVal, newVal) {

  }
  show() {
    this.logApi('show view', this.id);
    this.toggleClass('current', false, this);
  }
  hide() {
    this.logApi('hide view', this.id);
    this.toggleClass('hidden', true, this);
  }
  update() {
    this.logApi('update view', this.id);
  }
  currentView() {
    this.logApi('current view', this.id);
    this.child()[0].toggleClass('current', true, this);
  }
  nextView() {
    this.logApi('next view', this.id);
    this.toggleClass('next', true, this);
  }
  previousView() {
    this.logApi('previous view', this.id);
    this.toggleClass('previous', true, this);
  }
  contextChanged(newContext, oldContext) {
    this.logApi('contextChanged', newContext, oldContext);
  }

  //I handle loading a page from a url
  _tmplChanged(newVal, oldVal) {
    let _this = this,
      html = '';
    if (newVal) {
      this.logApi(this.id, 'Load remote html', newVal);
      this.importHref(newVal, e => {
        html = e.target.import.body.innerHTML;
        _this.logApi('inject html', _this.id);
        _this.logApi('inject px-view html', _this.id);
        _this.html(html);
      }, e => {
        // loading error
        _this.logApi('Error loading page', e);
      });
    }
  }
  showMenu() {
    px.mobile.dom('pxm-app').toggleClass('show-menu');
  }
  open() {
    this.logApi('open', this);
    this.addClass('active');
  }
  close() {
    this.logApi('close', this);
    this.removeClass('active');
  }
  toggle() {
    this.logApi('toggle', this);
    this.toggleClass('active');
  }
}

