  });

  it('mix - should mix (x) objects into one', function(done) {
    var cake = pxMobile.utils.mix({
      eggs: 2,
      large: true
    }, {
      butter: 1,
      salted: true
    }, {
      flour: '3 cups'
    }, {
      sugar: 'sure!'
    });
    assert.equal(cake.eggs, 2);
    assert.equal(cake.salted, true);
